import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Home from './Components/Home';
import Login from './Components/Login';
import Leaderboard from './Components/Leaderboard';
import Profile from './Components/Profile'
import './App.css';

function App() {
  const [leaderboardData, setLeaderboardData] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);
  const [tempDsaScore, setTempDsaScore] = useState(null);

  useEffect(() => {
    const storedUser = localStorage.getItem('currentUser');
    if (storedUser) {
      setCurrentUser(JSON.parse(storedUser));
    }
  }, []);

  const addUser = (user) => {
    const updatedLeaderboard = [...leaderboardData, user].sort((a, b) => b.dsaScore - a.dsaScore);
    updatedLeaderboard.forEach((user, index) => {
      user.rank = index + 1;
    });
    setLeaderboardData(updatedLeaderboard);
    setCurrentUser(user);
    localStorage.setItem('currentUser', JSON.stringify(user));
  };

  const handleLogout = () => {
    setCurrentUser(null);
    localStorage.removeItem('currentUser');
    if (window.google && window.google.accounts) {
      window.google.accounts.id.disableAutoSelect();
    }
  };

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <h1 className="App-title">DSA Leaderboard</h1>
          {currentUser && (
            <div className="user-info">
              <span>Welcome, {currentUser.name}</span>
              <button onClick={handleLogout}>Logout</button>
            </div>
          )}
        </header>
        <main className="App-content">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route 
              path="/profile" 
              element={<Profile setTempDsaScore={setTempDsaScore} />} 
            />
            <Route 
              path="/login" 
              element={
                tempDsaScore ? 
                <Login addUser={addUser} tempDsaScore={tempDsaScore} /> : 
                <Navigate to="/profile" />
              } 
            />
            <Route path="/leaderboard" element={<Leaderboard leaderboardData={leaderboardData} />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
