import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../AuthContext';
import './Leaderboard.css';

function Leaderboard() {
  const [leaderboardData, setLeaderboardData] = useState([]);
  const { user } = useAuth();

  useEffect(() => {
    // Simulate fetching leaderboard data
    // In a real application, you would fetch this data from your backend
    const storedData = localStorage.getItem('leaderboardData');
    if (storedData) {
      setLeaderboardData(JSON.parse(storedData));
    }
  }, []);

  useEffect(() => {
    if (user && user.dsaScore) {
      const updatedLeaderboard = [...leaderboardData];
      const existingUserIndex = updatedLeaderboard.findIndex(item => item.email === user.email);
      
      if (existingUserIndex !== -1) {
        updatedLeaderboard[existingUserIndex] = { ...user };
      } else {
        updatedLeaderboard.push(user);
      }

      // Sort and assign ranks
      updatedLeaderboard.sort((a, b) => b.dsaScore - a.dsaScore);
      updatedLeaderboard.forEach((item, index) => {
        item.rank = index + 1;
      });

      setLeaderboardData(updatedLeaderboard);
      localStorage.setItem('leaderboardData', JSON.stringify(updatedLeaderboard));
    }
  }, [user]);

  return (
    <div className="leaderboard-container">
      <h1>DSA Leaderboard</h1>
      <table className="leaderboard-table">
        <thead>
          <tr>
            <th>Rank</th>
            <th>Name</th>
            <th>Email</th>
            <th>Score</th>
          </tr>
        </thead>
        <tbody>
          {leaderboardData.map((user, index) => (
            <tr key={index}>
              <td>{user.rank}</td>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.dsaScore}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <Link to="/" className="back-button">Back To Home page</Link>
    </div>
  );
}

export default Leaderboard;
