import React from 'react';
import { GoogleLogin } from 'react-google-login';
import { useNavigate } from 'react-router-dom';

const clientId = "YOUR_GOOGLE_CLIENT_ID"; // Replace with your actual Google Client ID

function GoogleLoginComponent({ onLoginSuccess }) {
  const navigate = useNavigate();

  const onSuccess = (res) => {
    const email = res.profileObj.email;
    if (email.endsWith('@nsut.ac.in')) {
      onLoginSuccess(res.profileObj);
      navigate('/profile');
    } else {
      alert('Please use your NSUT email to login.');
    }
  };

  const onFailure = (res) => {
    console.log('Login failed: res:', res);
    alert('Google Sign-In was unsuccessful. Please try again later.');
  };

  return (
    <div>
      <GoogleLogin
        clientId={clientId}
        buttonText="Login with NSUT Google Account"
        onSuccess={onSuccess}
        onFailure={onFailure}
        cookiePolicy={'single_host_origin'}
        isSignedIn={true}
      />
    </div>
  );
}

export default GoogleLoginComponent;
