import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Home from './Components/Home';
import Login from './Components/Login';
import Leaderboard from './Components/Leaderboard';
import Profile from './Components/Profile';
import './App.css';
import { AuthProvider, useAuth } from './AuthContext';

function App() {
  const [leaderboardData, setLeaderboardData] = useState([]);
  const [tempDsaScore, setTempDsaScore] = useState(null);

  return (
    <AuthProvider>
      <Router>
        <div className="App">
          <header className="App-header">
            <h1 className="App-title">DSA Leaderboard</h1>
            <UserInfo />
          </header>
          <main className="App-content">
            <Routes>
              <Route path="/" element={<Home />} />
              <Route 
                path="/profile" 
                element={<PrivateRoute element={<Profile setTempDsaScore={setTempDsaScore} />} />} 
              />
              <Route 
                path="/login" 
                element={
                  <LoginWrapper addUser={addUser} tempDsaScore={tempDsaScore} />
                } 
              />
              <Route path="/leaderboard" element={<Leaderboard leaderboardData={leaderboardData} />} />
            </Routes>
          </main>
        </div>
      </Router>
    </AuthProvider>
  );
}

// Component to handle user info and logout
const UserInfo = () => {
  const { user, logout } = useAuth();

  return user ? (
    <div className="user-info">
      <span>Welcome, {user.name}</span>
      <button onClick={logout}>Logout</button>
    </div>
  ) : null;
};

// PrivateRoute component to protect routes
const PrivateRoute = ({ element }) => {
  const { user } = useAuth();
  return user ? element : <Navigate to="/login" />;
};

// Login wrapper to handle redirect after score is set
const LoginWrapper = ({ addUser, tempDsaScore }) => {
  return tempDsaScore ? <Login addUser={addUser} tempDsaScore={tempDsaScore} /> : <Navigate to="/profile" />;
};

export default App;
