import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import GoogleLogin from './GoogleLogin';
import { useAuth } from './AuthContext';
import './Login.css';

function Login() {
  const [email, setEmail] = useState('');
  const [name, setName] = useState('');
  const navigate = useNavigate();
  const { login } = useAuth();

  const handleLoginSuccess = (user) => {
    login(user);
    navigate('/');
  };

  const handleGoogleLoginSuccess = (googleUser) => {
    handleLoginSuccess(googleUser);
  };

  const handleEmailSubmit = (e) => {
    e.preventDefault();
    if (email.endsWith('@nsut.ac.in')) {
      handleLoginSuccess({ email, name });
    } else {
      alert('Please use your NSUT email to login.');
    }
  };

  return (
    <div className="login-container">
      <h1>Sign in</h1>
      <div className="login-options">
        <div className="google-login">
          <h2>Sign in with Google</h2>
          <GoogleLogin onLoginSuccess={handleGoogleLoginSuccess} />
        </div>
        <div className="divider">OR</div>
        <div className="email-login">
          <h2>Sign in with Email</h2>
          <form onSubmit={handleEmailSubmit}>
            <input
              type="email"
              placeholder="Enter your NSUT email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <input
              type="text"
              placeholder="Enter your name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
            <button type="submit">Sign In with Email</button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Login;
